import { Component } from '@angular/core';

@Component({
  selector: 'app-component3',
  templateUrl: './component3.component.html',
  styleUrls: ['./component3.component.scss'],
})
export class Component3Component {
  title: string = 'problem 8';
  isPressed: boolean = false;
  numberToFind: string = '';
  the1000DigitNumber: string =
    '';
  maxProd:number = 0;
  finalNumberAsCalculus: string='';

  resolveTheProblem(nr: number) {
    if (!this.isPressed) {
      let numberToVerify: string = '';
      let prod;
      while (this.the1000DigitNumber.length>=nr) {
        numberToVerify = this.the1000DigitNumber.substring(0, nr);
        prod = 1;
        for (let i = 0; i < numberToVerify.length; i++) {
          prod *= parseInt(numberToVerify[i]);
        }
        if (prod > this.maxProd) {
          this.maxProd = prod;
          this.numberToFind = numberToVerify;
        }
        this.the1000DigitNumber = this.the1000DigitNumber.substring(1);
      }
      for (let i = 0; i < this.numberToFind.length - 1; i++) {
        this.finalNumberAsCalculus += this.numberToFind[i] + ' Ã— ';
      }
      this.finalNumberAsCalculus +=
        this.numberToFind[this.numberToFind.length - 1];
      this.isPressed = true;
    }
  }
  
}
